/* hero-animation.css */

/* Gradient background pan */
@keyframes gradient-pan {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  .animate-gradient {
    background: linear-gradient(120deg, #1e3a8a, #4f46e5, #7c3aed);
    background-size: 200% 200%;
    animation: gradient-pan 15s ease infinite;
    position: absolute;
    inset: 0;
    z-index: 0;
  }

  /* Bottom waves */
  .hero-waves {
    height: 80px;
    overflow: hidden;
  }

  .hero-waves svg {
    width: 100%;
    height: 100%;
    fill: #000;
    opacity: 0.1;
  }

